static size_t hashtable_destroy_unsafe ( DICTIONARY * dict ) { pointer_destroy_index ( dict ) ; JError_t J_Error ; Word_t ret = JudyHSFreeArray ( & dict -> index . JudyHSArray , & J_Error ) ; if ( unlikely ( ret == ( Word_t ) JERR ) ) { error ( "DICTIONARY: Cannot destroy JudyHS, JU_ERRNO_* == %u, ID == %d" , JU_ERRNO ( & J_Error ) , JU_ERRID ( & J_Error ) ) ; } debug ( D_DICTIONARY , "Dictionary: hash table freed %lu bytes" , ret ) ; dict -> index . JudyHSArray = NULL ; return ( size_t ) ret ; } 