void feh_print_load_error ( char * file , winwidget w , Imlib_Load_Error err , enum feh_load_error feh_err ) { if ( err == IMLIB_LOAD_ERROR_OUT_OF_FILE_DESCRIPTORS ) { eprintf ( "%s - Out of file descriptors while loading" , file ) ; } if ( ! opt . quiet || w ) { switch ( feh_err ) { case LOAD_ERROR_IMLIB : break ; case LOAD_ERROR_IMAGEMAGICK : im_weprintf ( w , "%s - No ImageMagick loader for that file format" , file ) ; break ; case LOAD_ERROR_CURL : im_weprintf ( w , "%s - libcurl was unable to retrieve the file" , file ) ; break ; case LOAD_ERROR_DCRAW : im_weprintf ( w , "%s - Unable to open preview via dcraw" , file ) ; break ; case LOAD_ERROR_MAGICBYTES : im_weprintf ( w , "%s - Does not look like an image (magic bytes missing)" , file ) ; break ; } switch ( err ) { case IMLIB_LOAD_ERROR_FILE_DOES_NOT_EXIST : im_weprintf ( w , "%s - File does not exist" , file ) ; break ; case IMLIB_LOAD_ERROR_FILE_IS_DIRECTORY : im_weprintf ( w , "%s - Directory specified for image filename" , file ) ; break ; case IMLIB_LOAD_ERROR_PERMISSION_DENIED_TO_READ : im_weprintf ( w , "%s - No read access" , file ) ; break ; case IMLIB_LOAD_ERROR_UNKNOWN : case IMLIB_LOAD_ERROR_NO_LOADER_FOR_FILE_FORMAT : im_weprintf ( w , "%s - No Imlib2 loader for that file format" , file ) ; break ; case IMLIB_LOAD_ERROR_PATH_TOO_LONG : im_weprintf ( w , "%s - Path specified is too long" , file ) ; break ; case IMLIB_LOAD_ERROR_PATH_COMPONENT_NON_EXISTANT : im_weprintf ( w , "%s - Path component does not exist" , file ) ; break ; case IMLIB_LOAD_ERROR_PATH_COMPONENT_NOT_DIRECTORY : im_weprintf ( w , "%s - Path component is not a directory" , file ) ; break ; case IMLIB_LOAD_ERROR_PATH_POINTS_OUTSIDE_ADDRESS_SPACE : im_weprintf ( w , "%s - Path points outside address space" , file ) ; break ; case IMLIB_LOAD_ERROR_TOO_MANY_SYMBOLIC_LINKS : im_weprintf ( w , "%s - Too many levels of symbolic links" , file ) ; break ; case IMLIB_LOAD_ERROR_OUT_OF_MEMORY : im_weprintf ( w , "While loading %s - Out of memory" , file ) ; break ; case IMLIB_LOAD_ERROR_PERMISSION_DENIED_TO_WRITE : im_weprintf ( w , "%s - Cannot write to directory" , file ) ; break ; case IMLIB_LOAD_ERROR_OUT_OF_DISK_SPACE : im_weprintf ( w , "%s - Cannot write - out of disk space" , file ) ; break ; default : im_weprintf ( w , "While loading %s - Unknown error (%d)" , file , err ) ; break ; } } } 