static GElf_Sym sym_swap ( GElf_Sym * s1 , GElf_Sym * s2 ) { int t1 = GELF_ST_TYPE ( s1 -> st_info ) ; int t2 = GELF_ST_TYPE ( s2 -> st_info ) ; if ( ( t1 == STT_FUNC ) || ( t2 == STT_FUNC ) ) { return ( * s2 ) ; } if ( ( t1 == STT_OBJECT ) || ( t2 == STT_OBJECT ) ) { if ( t1 == STT_OBJECT ) { return ( * s1 ) ; } return ( * s2 ) ; } if ( ( t1 == STT_OBJECT ) || ( t2 == STT_OBJECT ) ) { if ( t1 == STT_OBJECT ) { return ( * s1 ) ; } return ( * s2 ) ; } return ( * s1 ) ; } 