void ff_hls_write_audio_rendition ( AVIOContext * out , const char * agroup , const char * filename , const char * language , int name_id , int is_default ) { avio_printf ( out , "#EXT-X-MEDIA:TYPE=AUDIO,GROUP-ID=\"group_%s\"" , agroup ) ; avio_printf ( out , ",NAME=\"audio_%d\",DEFAULT=%s," , name_id , is_default ?"YES" : "NO" ) ; if ( language ) { avio_printf ( out , "LANGUAGE=\"%s\"," , language ) ; } avio_printf ( out , "URI=\"%s\"\n" , filename ) ; } 