static void filter_by_sm ( struct sm_state * sm , struct state_list * * true_stack , struct state_list * * false_stack ) { if ( sm -> state == & true_path ) { add_ptr_list ( true_stack , sm ) ; } if ( sm -> state == & false_path ) { add_ptr_list ( false_stack , sm ) ; } if ( sm -> merged ) { filter_by_sm ( sm -> left , true_stack , false_stack ) ; filter_by_sm ( sm -> right , true_stack , false_stack ) ; } } 