static void manager_log ( isc__socketmgr_t * sockmgr , isc_logcategory_t * category , isc_logmodule_t * module , int level , const char * fmt , ... ) { int msgbuf [ 2048 ] ; va_list ap ; if ( ! isc_log_wouldlog ( isc_lctx , level ) ) { return ; } va_start ( ap , fmt ) ; vsnprintf ( msgbuf , sizeof ( msgbuf ) , fmt , ap ) ; va_end ( ap ) ; isc_log_write ( isc_lctx , category , module , level , "sockmgr %p: %s" , sockmgr , msgbuf ) ; } 