void lspcon_resume ( struct intel_lspcon * lspcon ) { enum drm_lspcon_mode expected_mode ; if ( lspcon_wake_native_aux_ch ( lspcon ) ) { expected_mode = DRM_LSPCON_MODE_PCON ; lspcon_resume_in_pcon_wa ( lspcon ) ; } else { expected_mode = DRM_LSPCON_MODE_LS ; } if ( lspcon_change_mode ( lspcon , DRM_LSPCON_MODE_PCON ) ) { DRM_ERROR ( "LSPCON resume failed\n" ) ; } else { DRM_DEBUG_KMS ( "LSPCON resume success\n" ) ; } } 