static int hdmi_dump_regs ( struct seq_file * s , void * p ) { struct omap_hdmi * hdmi = s -> private ; mutex_lock ( & hdmi -> lock ) ; if ( hdmi_runtime_get ( hdmi ) ) { return 0 ; } hdmi_wp_dump ( & hdmi -> wp , s ) ; hdmi_pll_dump ( & hdmi -> pll , s ) ; hdmi_phy_dump ( & hdmi -> phy , s ) ; hdmi4_core_dump ( & hdmi -> core , s ) ; hdmi_runtime_put ( hdmi ) ; mutex_unlock ( & hdmi -> lock ) ; return 0 ; } 