int uv_random ( uv_loop_t * loop , uv_random_t * req , void * buf , size_t buflen , unsigned flags , uv_random_cb cb ) { if ( flags != 0 ) { return UV_EINVAL ; } if ( cb == NULL ) { return uv__random ( buf , buflen ) ; } uv__req_init ( loop , req , UV_RANDOM ) ; req -> loop = loop ; req -> status = 0 ; req -> cb = cb ; req -> buf = buf ; req -> buflen = buflen ; uv__work_submit ( loop , & req -> work_req , UV__WORK_CPU , uv__random_work , uv__random_done ) ; return 0 ; } 