int rasops_vcons_erasecols ( void * cookie , int row , int col , int num , uint32_t attr ) { struct rasops_screen * scr = cookie ; int cols = scr -> rs_ri -> ri_cols ; int i ; for ( i = 0 ; i < num ; i ++ ) { int off = row * cols + col + i + scr -> rs_dispoffset ; scr -> rs_bs [ off ] . uc = ' ' ; scr -> rs_bs [ off ] . attr = attr ; } return scr -> rs_ri -> ri_erasecols ( scr -> rs_ri , row , col , num , attr ) ; } 