krb5_error_code krb5_db_store_master_key_list ( krb5_context kcontext , char * keyfile , krb5_principal mname , char * master_pwd ) { krb5_error_code status = 0 ; kdb_vftabl * v ; status = get_vftabl ( kcontext , & v ) ; if ( v -> store_master_key_list == NULL ) { return KRB5_KDB_DBTYPE_NOSUP ; } if ( kcontext -> dal_handle -> master_keylist == NULL ) { return KRB5_KDB_DBNOTINITED ; } return v -> store_master_key_list ( kcontext , keyfile , mname , kcontext -> dal_handle -> master_keylist , master_pwd ) ; } 