static void get_framebits ( lame_internal_flags * gfc , int frameBits [ 15 ] ) { SessionConfig_t const * const cfg = & gfc -> cfg ; EncResult_t * const eov = & gfc -> ov_enc ; int bitsPerFrame , i ; eov -> bitrate_index = cfg -> vbr_min_bitrate_index ; bitsPerFrame = getframebits ( gfc , NULL ) ; eov -> bitrate_index = 1 ; bitsPerFrame = getframebits ( gfc ) ; for ( i = 1 ; i <= cfg -> vbr_max_bitrate_index ; i ++ ) { eov -> bitrate_index = i ; frameBits [ i ] = ResvFrameBegin ( gfc , & bitsPerFrame ) ; } } 