void init_font_rule ( unsigned tab ) { if ( tab >= MAX_EXTRA_TABLES ) { internal_error_1 ( "init_font_rule (tab=%d)" , tab ) ; } if ( rule_tables [ tab ] != NULL ) { warning_1 ( "RULE table (D %d) previously defined; " "old value ignored" , tab ) ; } rule_tables [ tab ] = ( rule * ) xcalloc ( MAX_TABLE_ENTRIES , sizeof ( rule ) ) ; if ( tab >= no_rule_tables ) { no_rule_tables = tab + 1 ; } cur_rule_table_index = tab ; cur_rule_table = rule_tables [ tab ] ; max_rule_entry [ tab ] = 0 ; } 