void dcn32_set_pixels_per_cycle ( struct pipe_ctx * pipe_ctx ) { uint32_t pix_per_cycle = 1 ; uint32_t odm_combine_factor = 1 ; odm_combine_factor = get_odm_config ( pipe_ctx , NULL ) ; if ( optc2_is_two_pixels_per_containter ( & pipe_ctx -> stream -> timing ) || odm_combine_factor > 1 || dcn32_is_dp_dig_pixel_rate_div_policy ( pipe_ctx ) ) { pix_per_cycle = 2 ; } if ( pipe_ctx -> stream_res . stream_enc -> funcs -> set_input_mode ) { pipe_ctx -> stream_res . stream_enc -> funcs -> set_input_mode ( pipe_ctx -> stream_res . stream_enc , pix_per_cycle ) ; } } 