struct blockvector * blockvector_for_pc_sect ( register CORE_ADDR pc , struct symtab * symtab ) { register struct block * b ; register int bot , top , half ; struct blockvector * bl ; bl = symtab -> blockvector ; b = bl -> block [ 0 ] ; bot = 0 ; top = bl -> nblocks ; while ( top - bot > 1 ) { half = ( top - bot + 1 ) >> 1 ; b = bl -> block [ bot + half ] ; if ( b -> startaddr <= pc ) { bot += half ; } else { top = bot + half ; } } while ( bot >= 0 ) { b = bl -> block [ bot ] ; bot -- ; } return 0 ; } 