rpmRC processBinaryPolicies ( rpmSpec spec , int test ) { rpmRC rc = RPMRC_OK ; Package pkg ; char * nvr ; for ( pkg = spec -> packages ; pkg != NULL ; pkg = pkg -> next ) { if ( pkg -> policyList == NULL ) { continue ; } nvr = headerGetAsString ( pkg -> header , RPMTAG_NVRA ) ; rpmlog ( RPMLOG_NOTICE , _ ( "Processing policies: %s\n" ) , nvr ) ; if ( processPolicies ( spec , pkg , test ) != RPMRC_OK ) { rc = RPMRC_FAIL ; break ; } } return rc ; } 