bool ovs_ct_verify ( struct net * net , enum ovs_key_attr attr ) { if ( attr == OVS_KEY_ATTR_CT_STATE ) { return true ; } if ( IS_ENABLED ( CONFIG_NF_CONNTRACK_MARK ) && attr == OVS_KEY_ATTR_CT_MARK ) { return true ; } if ( IS_ENABLED ( CONFIG_NF_CONNTRACK_LABELS ) && attr == OVS_KEY_ATTR_CT_LABELS ) { struct ovs_net * ovs_net = net_generic ( net , ovs_net_id ) ; return ovs_net -> xt_label ; } return false ; } 