int ASN1_INTEGER_get_uint64 ( uint64_t * out_val , const ASN1_INTEGER * aint ) { uint64_t val ; CBS cbs ; * out_val = 0 ; if ( aint -> type == V_ASN1_NEG_INTEGER ) { ASN1error ( ASN1_R_ILLEGAL_NEGATIVE_VALUE ) ; return 0 ; } if ( aint -> type != V_ASN1_INTEGER ) { ASN1error ( ASN1_R_WRONG_INTEGER_TYPE ) ; return 0 ; } CBS_init ( & cbs , aint -> data , aint -> length ) ; if ( ! asn1_aint_get_uint64 ( & cbs , & val ) ) { return 0 ; } * out_val = val ; return 1 ; } 