int ixgbe_rx_ring_stat ( mac_ring_driver_t rh , uint_t stat , uint64_t * val ) { ixgbe_rx_ring_t * rx_ring = ( ixgbe_rx_ring_t * ) rh ; ixgbe_t * ixgbe = rx_ring -> ixgbe ; switch ( stat ) { case MAC_STAT_RBYTES : * val = rx_ring -> stat_rbytes ; break ; case MAC_STAT_IPACKETS : * val = rx_ring -> stat_ipackets ; break ; default : * val = 0 ; return ( ENOTSUP ) ; } return ( 0 ) ; } 