static u_int16_t AscGetChipBusType ( bus_space_tag_t iot , bus_space_handle_t ioh ) { u_int16_t chip_ver ; chip_ver = ASC_GET_CHIP_VER_NO ( iot , ioh ) ; if ( ( chip_ver >= ASC_CHIP_MIN_VER_ISA ) && ( chip_ver <= ASC_CHIP_MAX_VER_ISA ) ) { if ( chip_ver >= ASC_CHIP_MIN_VER_ISA_PNP ) { return ( ASC_IS_ISAPNP ) ; } return ( ASC_IS_ISA ) ; } if ( ( chip_ver >= ASC_CHIP_MIN_VER_PCI ) && ( chip_ver <= ASC_CHIP_MAX_VER_PCI ) ) { return ( ASC_IS_PCI ) ; } return ( 0 ) ; } 