static int select_del ( struct event_base * base , int fd , short old , short events , void * p ) { struct selectop * sop = base -> evbase ; ( void ) p ; EVUTIL_ASSERT ( ( events & EV_SIGNAL ) == 0 ) ; check_selectop ( sop ) ; if ( sop -> event_fds < fd ) { return ( 0 ) ; } if ( events & EV_READ ) { FD_CLR ( fd , sop -> event_readset_in ) ; } if ( events & EV_WRITE ) { FD_CLR ( fd , sop -> event_writeset_in ) ; } check_selectop ( sop ) ; return ( 0 ) ; } 