static int bmg160_trig_try_reen ( struct iio_trigger * trig ) { struct iio_dev * indio_dev = iio_trigger_get_drvdata ( trig ) ; struct bmg160_data * data = iio_priv ( indio_dev ) ; struct device * dev = regmap_get_device ( data -> regmap ) ; int ret ; ret = regmap_write ( data -> regmap , BMG160_REG_INT_RST_LATCH , BMG160_INT_MODE_LATCH_INT | BMG160_INT_MODE_LATCH_RESET ) ; if ( ret < 0 ) { dev_err ( dev , "Error writing reg_rst_latch\n" ) ; return ret ; } return 0 ; } 