int ObjCopyAttr ( struct worker * wrk , struct objcore * oc , struct objcore * ocs , enum obj_attr attr ) { const void * vps ; void * vpd ; ssize_t l ; CHECK_OBJ_NOTNULL ( wrk , WORKER_MAGIC ) ; CHECK_OBJ_NOTNULL ( oc , OBJCORE_MAGIC ) ; CHECK_OBJ_NOTNULL ( oc -> boc , BOC_MAGIC ) ; CHECK_OBJ_NOTNULL ( ocs , OBJCORE_MAGIC ) ; vps = ObjGetAttr ( wrk , ocs , attr , & l ) ; vpd = ObjSetAttr ( wrk , oc , attr , l , vps ) ; if ( vpd == NULL ) { return ( - 1 ) ; } return ( 0 ) ; } 