static int op_sidebar_first ( struct SidebarWindowData * wdata , int op ) { if ( ! mutt_window_is_visible ( wdata -> win ) ) { return FR_NO_ACTION ; } int orig_hil_index = wdata -> hil_index ; wdata -> hil_index = 0 ; if ( ( * ARRAY_GET ( & wdata -> entries , wdata -> hil_index ) ) -> is_hidden ) { if ( ! sb_next ( wdata ) ) { wdata -> hil_index = orig_hil_index ; } } if ( orig_hil_index == wdata -> hil_index ) { return FR_NO_ACTION ; } wdata -> win -> actions |= WA_RECALC ; return FR_SUCCESS ; } 