const struct vsp1_format_info * vsp1_get_format_info ( struct vsp1_device * vsp1 , u32 fourcc ) { unsigned int i ; if ( vsp1 -> info -> gen != 2 ) { switch ( fourcc ) { case V4L2_PIX_FMT_VYUY : case V4L2_PIX_FMT_HSV24 : case V4L2_PIX_FMT_HSV32 : return NULL ; } } for ( i = 0 ; i < ARRAY_SIZE ( vsp1_video_formats ) ; ++ i ) { const struct vsp1_format_info * info = & vsp1_video_formats [ i ] ; } return NULL ; } 