void msCloseErrorFile ( ) { debugInfoObj * debuginfo = msGetDebugInfoObj ( ) ; if ( debuginfo && debuginfo -> debug_mode != MS_DEBUGMODE_OFF ) { if ( debuginfo -> fp && debuginfo -> debug_mode == MS_DEBUGMODE_FILE ) { fclose ( debuginfo -> fp ) ; } if ( debuginfo -> fp && ( debuginfo -> debug_mode == MS_DEBUGMODE_STDERR || debuginfo -> debug_mode == MS_DEBUGMODE_STDOUT ) ) { fflush ( debuginfo -> fp ) ; } debuginfo -> fp = NULL ; msFree ( debuginfo -> errorfile ) ; debuginfo -> debug_mode = MS_DEBUGMODE_OFF ; } } 