struct acpi_namespace_node * acpi_db_local_ns_lookup ( char * name ) { char * internal_path ; acpi_status status ; struct acpi_namespace_node * node = NULL ; acpi_db_prep_namestring ( name ) ; status = acpi_ns_internalize_name ( name , & internal_path ) ; if ( ACPI_FAILURE ( status ) ) { acpi_os_printf ( "Invalid namestring: %s\n" , name ) ; return ( NULL ) ; } status = acpi_ns_lookup ( NULL , internal_path , ACPI_TYPE_ANY , ACPI_IMODE_EXECUTE , ACPI_NS_NO_UPSEARCH | ACPI_NS_DONT_OPEN_SCOPE , NULL , & node ) ; if ( ACPI_FAILURE ( status ) ) { acpi_os_printf ( "Could not locate name: %s, %s\n" , name , acpi_format_exception ( status ) ) ; } ACPI_FREE ( internal_path , NULL ) ; return ( node ) ; } 