static struct config_item * uvcg_control_header_make ( struct config_group * group , const char * name ) { struct uvcg_control_header * h ; h = kmalloc ( sizeof ( * h ) , GFP_KERNEL ) ; if ( ! h ) { return ERR_PTR ( - ENOMEM ) ; } h -> desc . bLength = UVC_DT_HEADER_SIZE ( 1 ) ; h -> desc . bDescriptorType = USB_DT_CS_INTERFACE ; h -> desc . bDescriptorSubType = UVC_VC_HEADER ; h -> desc . bcdUVC = cpu_to_le16 ( 0x0100 ) ; h -> desc . dwClockFrequency = cpu_to_le32 ( 48000000 ) ; config_item_init_type_name ( & h -> item , name , & uvcg_control_header_type ) ; return & h -> item ; } 