static int mdp5_crtc_setup_pipeline ( struct drm_crtc * crtc , struct drm_crtc_state * new_crtc_state , bool need_right_mixer ) { struct mdp5_crtc_state * mdp5_cstate = to_mdp5_crtc_state ( new_crtc_state ) ; struct mdp5_pipeline * pipeline = & mdp5_cstate -> pipeline ; struct mdp5_interface * intf ; bool new_mixer = false ; new_mixer = ! pipeline -> mixer ; if ( ( need_right_mixer && ! pipeline -> r_mixer ) || ( ! need_right_mixer && pipeline -> r_mixer ) ) { new_mixer = true ; } if ( new_mixer ) { struct mdp5_hw_mixer * old_mixer = pipeline -> mixer ; struct mdp5_hw_mixer * old_r_mixer = pipeline -> r_mixer ; u32 caps ; int ret ; caps = MDP_LM_CAP_DISPLAY ; if ( need_right_mixer ) { caps |= MDP_LM_CAP_PAIR ; } ret = mdp5_mixer_assign ( new_crtc_state -> state , crtc , caps , & pipeline -> mixer , need_right_mixer ?& pipeline -> r_mixer : NULL ) ; ret = mdp5_mixer_release ( new_crtc_state -> state , old_mixer ) ; if ( ret ) { return ret ; } if ( old_r_mixer ) { ret = mdp5_mixer_release ( new_crtc_state -> state , old_r_mixer ) ; if ( ret ) { return ret ; } if ( ! need_right_mixer ) { pipeline -> r_mixer = NULL ; } } } intf = pipeline -> intf ; mdp5_cstate -> err_irqmask = intf2err ( intf -> num ) ; mdp5_cstate -> vblank_irqmask = intf2vblank ( pipeline -> mixer , intf ) ; if ( ( intf -> type == INTF_DSI ) && ( intf -> mode == MDP5_INTF_DSI_MODE_COMMAND ) ) { mdp5_cstate -> pp_done_irqmask = lm2ppdone ( pipeline -> mixer ) ; mdp5_cstate -> cmd_mode = true ; } else { mdp5_cstate -> pp_done_irqmask = 0 ; mdp5_cstate -> cmd_mode = false ; } return 0 ; } 