static void netlink_determine_zebra_iftype ( const char * kind , enum zebra_iftype * zif_type ) { * zif_type = ZEBRA_IF_OTHER ; if ( strcmp ( kind , "vrf" ) == 0 ) { * zif_type = ZEBRA_IF_VRF ; } if ( strcmp ( kind , "bridge" ) == 0 ) { * zif_type = ZEBRA_IF_BRIDGE ; } if ( strcmp ( kind , "vlan" ) == 0 ) { * zif_type = ZEBRA_IF_VLAN ; } if ( strcmp ( kind , "vxlan" ) == 0 ) { * zif_type = ZEBRA_IF_VXLAN ; } if ( strcmp ( kind , "macvlan" ) == 0 ) { * zif_type = ZEBRA_IF_MACVLAN ; } if ( strcmp ( kind , "veth" ) == 0 ) { * zif_type = ZEBRA_IF_VETH ; } if ( strcmp ( kind , "bond" ) == 0 ) { * zif_type = ZEBRA_IF_BOND ; } if ( strcmp ( kind , "bond_slave" ) == 0 ) { * zif_type = ZEBRA_IF_BOND_SLAVE ; } if ( strcmp ( kind , "gre" ) == 0 ) { * zif_type = ZEBRA_IF_GRE ; } } 