static void cas_get_ethtool_stats ( struct net_device * dev , struct ethtool_stats * estats , u64 * data ) { struct cas * cp = netdev_priv ( dev ) ; struct net_device_stats * stats = cas_get_stats ( cp -> dev ) ; int i ; data [ i ++ ] = stats -> collisions ; data [ i ++ ] = stats -> rx_bytes ; data [ i ++ ] = stats -> rx_crc_errors ; data [ i ++ ] = stats -> rx_dropped ; data [ i ++ ] = stats -> rx_errors ; data [ i ++ ] = stats -> rx_fifo_errors ; data [ i ++ ] = stats -> rx_frame_errors ; data [ i ++ ] = stats -> rx_length_errors ; data [ i ++ ] = stats -> rx_over_errors ; data [ i ++ ] = stats -> rx_packets ; data [ i ++ ] = stats -> tx_aborted_errors ; data [ i ++ ] = stats -> tx_bytes ; data [ i ++ ] = stats -> tx_dropped ; data [ i ++ ] = stats -> tx_errors ; data [ i ++ ] = stats -> tx_fifo_errors ; data [ i ++ ] = stats -> tx_packets ; BUG_ON ( i != CAS_NUM_STAT_KEYS ) ; } 