static void sig_channel_joined ( CHANNEL_REC * channel ) { NICK_REC * nick ; char * nickname ; nickname = g_hash_table_lookup ( printnicks , channel -> ownnick ) ; if ( nickname == NULL ) { return ; } g_hash_table_remove ( printnicks , channel -> ownnick ) ; nick = channel -> ownnick -> next ; while ( nick != NULL ) { if ( g_hash_table_lookup ( printnicks , nick ) == NULL ) { sig_nicklist_new ( channel , nick ) ; break ; } nick = nick -> next ; } } 