void bus_track_dispatch ( sd_bus_track * track ) { int r ; assert ( track ) ; assert ( track -> handler ) ; bus_track_remove_from_queue ( track ) ; sd_bus_track_ref ( track , NULL ) ; r = track -> handler ( track , track -> userdata ) ; if ( r < 0 ) { log_debug_errno ( r , "Failed to process track handler: %m" ) ; } if ( r == 0 ) { bus_track_add_to_queue ( track ) ; } sd_bus_track_unref ( track ) ; } 