void ssl_init_ocsp_certificates ( server_rec * s , modssl_ctx_t * mctx ) { if ( ! mctx -> ocsp_certs_file ) { return ; } ap_log_error ( APLOG_MARK , APLOG_DEBUG , 0 , s , "Configuring Trusted OCSP certificates" ) ; mctx -> ocsp_certs = modssl_read_ocsp_certificates ( mctx -> ocsp_certs_file , NULL ) ; if ( ! mctx -> ocsp_certs ) { ap_log_error ( APLOG_MARK , APLOG_ERR , 0 , s , "Unable to configure OCSP Trusted Certificates" ) ; ssl_log_ssl_error ( SSLLOG_MARK , APLOG_ERR , s ) ; ssl_die ( s ) ; } mctx -> ocsp_verify_flags |= OCSP_TRUSTOTHER ; } 