static void r2cmd ( int in , int out , const char * cmd ) { size_t cmd_len = strlen ( cmd ) + 1 ; if ( write ( out , cmd , cmd_len ) != cmd_len ) { return ; } if ( write ( out , "\n" , 1 ) != 1 ) { return ; } int bufsz = ( 1024 * 64 ) ; unsigned char * buf = malloc ( bufsz ) ; if ( ! buf ) { return ; } while ( 1 ) { int n = read ( in , buf , bufsz ) ; if ( n != bufsz ) { break ; } buf [ bufsz - 1 ] = '\0' ; int len = strlen ( ( const char * ) buf ) ; n = len ; if ( n < 1 ) { break ; } n = write ( 1 , buf , n ) ; if ( n != bufsz ) { break ; } } write ( 1 , "\n" , 1 ) ; } 