void ieee80211_led_exit ( struct ieee80211_local * local ) { if ( local -> radio_led . name ) { led_trigger_unregister ( & local -> radio_led ) ; } if ( local -> assoc_led . name ) { led_trigger_unregister ( & local -> assoc_led ) ; } if ( local -> tx_led . name ) { led_trigger_unregister ( & local -> tx_led ) ; } if ( local -> rx_led . name ) { led_trigger_unregister ( & local -> rx_led ) ; } if ( local -> tpt_led_trigger ) { led_trigger_unregister ( & local -> tpt_led ) ; } } 