int apple_rtkit_quiesce ( struct apple_rtkit * rtk ) { int ret ; ret = apple_rtkit_set_ap_power_state ( rtk , APPLE_RTKIT_PWR_STATE_QUIESCED ) ; ret = apple_rtkit_set_iop_power_state ( rtk , APPLE_RTKIT_PWR_STATE_QUIESCED ) ; if ( ret ) { return ret ; } ret = apple_rtkit_reinit ( rtk ) ; if ( ret ) { return ret ; } rtk -> iop_power_state = APPLE_RTKIT_PWR_STATE_QUIESCED ; rtk -> ap_power_state = APPLE_RTKIT_PWR_STATE_QUIESCED ; return 0 ; } 