struct drm_device * drm_dev_alloc ( struct drm_driver * driver , struct device * parent ) { struct drm_device * dev ; int ret ; dev = kmalloc ( sizeof ( * dev ) , GFP_KERNEL ) ; if ( ! dev ) { return ERR_PTR ( - ENOMEM ) ; } ret = drm_dev_init ( dev , driver , parent ) ; if ( ret ) { kfree ( dev ) ; return ERR_PTR ( ret ) ; } return dev ; } 