void xchk_xref_is_not_shared ( struct xfs_scrub * sc , xfs_agblock_t agbno , xfs_extlen_t len ) { bool shared ; int error ; error = xfs_refcount_has_record ( sc -> sa . refc_cur , XFS_REFC_DOMAIN_SHARED , agbno , len , & shared ) ; if ( ! xchk_should_check_xref ( sc , & error , & sc -> sa . refc_cur ) ) { return ; } if ( shared ) { xchk_btree_xref_set_corrupt ( sc , sc -> sa . refc_cur , 0 ) ; } } 