int WindingOnPlaneSide ( winding_t * w , vec3_t normal , vec_t dist ) { qboolean front , back ; int i ; vec_t d ; front = qfalse ; back = qfalse ; for ( i = 0 ; i < w -> numpoints ; i ++ ) { d = DotProduct ( w -> p [ i ] , normal ) - dist ; if ( d < - ON_EPSILON ) { back = qtrue ; continue ; } if ( d > ON_EPSILON ) { if ( back ) { return SIDE_CROSS ; } front = qtrue ; continue ; } } if ( back ) { return SIDE_BACK ; } if ( front ) { return SIDE_FRONT ; } return SIDE_ON ; } 