static int iadc_poll_wait_eoc ( struct iadc_chip * iadc , unsigned int interval_us ) { unsigned int count , retry ; int ret ; u8 sta1 ; retry = interval_us / IADC_CONV_TIME_MIN_US ; for ( count = 0 ; count < retry ; count ++ ) { ret = iadc_read ( iadc , IADC_STATUS1 , & sta1 ) ; if ( ret < 0 ) { return ret ; } sta1 &= IADC_STATUS1_REQ_STS_EOC_MASK ; usleep_range ( IADC_CONV_TIME_MIN_US , IADC_CONV_TIME_MAX_US ) ; } iadc_status_show ( iadc ) ; return - ETIMEDOUT ; } 