int inet_csk_listen_start ( struct sock * sk , int backlog ) { struct inet_connection_sock * icsk = inet_csk ( sk ) ; struct inet_sock * inet = inet_sk ( sk ) ; int err = - EADDRINUSE ; reqsk_queue_alloc ( & icsk -> icsk_accept_queue ) ; sk -> sk_max_ack_backlog = backlog ; sk -> sk_ack_backlog = 0 ; inet_csk_delack_init ( sk ) ; sk_state_store ( sk , TCP_LISTEN ) ; if ( ! sk -> sk_prot -> get_port ( sk , inet -> inet_num ) ) { inet -> inet_sport = htons ( inet -> inet_num ) ; sk_dst_reset ( sk ) ; err = sk -> sk_prot -> hash ( sk ) ; } sk -> sk_state = TCP_CLOSE ; return err ; } 