static int stmpe1601_enable ( struct stmpe * stmpe , unsigned int blocks , bool enable ) { int mask = 0 ; if ( blocks & STMPE_BLOCK_GPIO ) { mask |= STMPE1601_SYS_CTRL_ENABLE_GPIO ; } else { mask &= ~ STMPE1601_SYS_CTRL_ENABLE_GPIO ; } if ( blocks & STMPE_BLOCK_KEYPAD ) { mask |= STMPE1601_SYS_CTRL_ENABLE_KPC ; } else { mask &= ~ STMPE1601_SYS_CTRL_ENABLE_KPC ; } if ( blocks & STMPE_BLOCK_PWM ) { mask |= STMPE1601_SYS_CTRL_ENABLE_SPWM ; } else { mask &= ~ STMPE1601_SYS_CTRL_ENABLE_SPWM ; } return __stmpe_set_bits ( stmpe , stmpe -> regs [ STMPE_IDX_SYS_CTRL ] , mask , enable ?mask : 0 ) ; } 