static int himax_check_product_id ( struct himax_ts_data * ts ) { int error ; u32 product_id ; error = himax_read_product_id ( ts , & product_id ) ; dev_dbg ( & ts -> client -> dev , "Product id: %x\n" , product_id ) ; switch ( product_id ) { case HIMAX_ID_83112B : return 0 ; default : dev_err ( & ts -> client -> dev , "Unknown product id: %x\n" , product_id ) ; return - EINVAL ; } } 