static long tls_bio_mbuf_ctrl ( WOLFSSL_BIO * b , int cmd , long arg1 , void * arg2 ) { long ret ; switch ( cmd ) { case BIO_CTRL_GET_CLOSE : case BIO_CTRL_SET_CLOSE : ret = 0 ; break ; case BIO_CTRL_DUP : case BIO_CTRL_FLUSH : ret = 1 ; break ; case BIO_CTRL_RESET : case BIO_CTRL_INFO : case BIO_CTRL_PENDING : case BIO_CTRL_WPENDING : default : ret = 0 ; break ; } TLS_BIO_DBG ( "ctrl called (%p, %d, %ld, %p) =>%ld\n" , b , cmd , arg1 , arg2 , ret ) ; return ret ; } 