efi_status_t efi_query_variable_store ( u32 attributes , unsigned long size , bool nonblocking ) { efi_status_t status ; u64 storage_size , remaining_size , max_size ; if ( ! ( attributes & EFI_VARIABLE_NON_VOLATILE ) ) { return 0 ; } if ( nonblocking ) { return query_variable_store_nonblocking ( attributes , size ) ; } status = efi . query_variable_info ( attributes , & storage_size , & remaining_size , & max_size ) ; if ( status != EFI_SUCCESS ) { return status ; } if ( ( remaining_size - size < EFI_MIN_RESERVE ) && ! efi_no_storage_paranoia ) { unsigned long dummy_size = remaining_size + 1024 ; void * dummy = kzalloc ( dummy_size , GFP_ATOMIC ) ; if ( ! dummy ) { return EFI_OUT_OF_RESOURCES ; } status = efi . set_variable ( efi_dummy_name , & EFI_DUMMY_GUID , EFI_VARIABLE_NON_VOLATILE | EFI_VARIABLE_BOOTSERVICE_ACCESS | EFI_VARIABLE_RUNTIME_ACCESS , dummy_size , dummy ) ; if ( status == EFI_SUCCESS ) { efi_delete_dummy_variable ( ) ; } status = efi . query_variable_info ( attributes , & storage_size , & remaining_size , & max_size ) ; if ( status != EFI_SUCCESS ) { return status ; } if ( remaining_size - size < EFI_MIN_RESERVE ) { return EFI_OUT_OF_RESOURCES ; } } return EFI_SUCCESS ; } 