int git_pack__lookup_id ( const void * oid_lookup_table , size_t stride , unsigned lo , unsigned hi , const unsigned char * oid_prefix , const git_oid_t oid_type ) { const unsigned char * base = oid_lookup_table ; size_t oid_size = git_oid_size ( oid_type ) ; while ( lo < hi ) { unsigned mi = ( lo + hi ) / 2 ; int cmp = git_oid_raw_cmp ( base + mi * stride , oid_prefix , oid_size ) ; if ( cmp > 0 ) { hi = mi ; } else { lo = mi + 1 ; } } return - ( ( int ) lo ) - 1 ; } 