static void refresh_index_quietly ( void ) { struct lock_file lock_file = LOCK_INIT ; int fd ; fd = repo_hold_locked_index ( the_repository , & lock_file , 0 ) ; if ( fd < 0 ) { return ; } discard_index ( & the_index ) ; repo_read_index ( the_repository ) ; refresh_index ( & the_index , REFRESH_QUIET | REFRESH_UNMERGED , NULL , NULL ) ; repo_update_index_if_able ( the_repository , & lock_file ) ; } 