int xadc_read_event_value ( struct iio_dev * indio_dev , const struct iio_chan_spec * chan , enum iio_event_type type , enum iio_event_direction dir , enum iio_event_info info , int * val , int * val2 ) { int offset = xadc_get_threshold_offset ( chan , dir ) ; struct xadc * xadc = iio_priv ( indio_dev ) ; switch ( info ) { case IIO_EV_INFO_VALUE : * val = xadc -> threshold [ offset ] ; break ; case IIO_EV_INFO_HYSTERESIS : * val = xadc -> temp_hysteresis ; break ; default : return - EINVAL ; } * val >>= XADC_THRESHOLD_VALUE_SHIFT ; return IIO_VAL_INT ; } 