static void aemif_get_hw_params ( struct platform_device * pdev , int csnum ) { struct aemif_device * aemif = platform_get_drvdata ( pdev ) ; struct aemif_cs_data * data = & aemif -> cs_data [ csnum ] ; unsigned long clk_rate = aemif -> clk_rate ; u32 val , offset ; offset = A1CR_OFFSET + ( data -> cs - aemif -> cs_offset ) * 4 ; val = readl ( aemif -> base + offset ) ; data -> ta = aemif_cycles_to_nsec ( TA_VAL ( val ) , clk_rate ) ; data -> rstrobe = aemif_cycles_to_nsec ( RSTROBE_VAL ( val ) , clk_rate ) ; data -> rsetup = aemif_cycles_to_nsec ( RSETUP_VAL ( val ) , clk_rate ) ; data -> whold = aemif_cycles_to_nsec ( WHOLD_VAL ( val ) , clk_rate ) ; data -> wstrobe = aemif_cycles_to_nsec ( WSTROBE_VAL ( val ) , clk_rate ) ; data -> wsetup = aemif_cycles_to_nsec ( WSETUP_VAL ( val ) , clk_rate ) ; data -> enable_ew = EW_VAL ( val ) ; data -> enable_ss = SSTROBE_VAL ( val ) ; data -> asize = val & ASIZE_MAX ; } 